labels.HAPROXY_0_HTTPS_FRONTEND_ACL_WITH_AUTH:
acl host_{cleanedUpHostname} hdr(host) -i {hostname} 
  acl auth_{cleanedUpHostname} http_auth(user_{backend}) 
 acl is_api_{cleanedUpHostname} path_reg '^/(assets)/?' 
  http-request auth realm "{realm}" if host_{cleanedUpHostname} !is_api_{cleanedUpHostname} !auth_{cleanedUpHostname} 
  use_backend {backend} if host_{cleanedUpHostname}
